mapper.select:
select count(*)
from book
where book_name = #{bookName}

mapper.update:
update book
set total_quantity    = total_quantity + #{regQuantity},
    loanable_quantity = loanable_quantity + #{regQuantity}
where book_name = #{bookName}

update book
set total_quantity = #{totalQuantity}, loanable_quantity = #{loanableQuantity}
where book_name = #{bookName}
